   const inputEvent = (event) => {
      console.log(event.target.value)
      setName(event.target.value);
   }

   const onsumbit = () => {
      setFullName(name);
   }



  return (
    <>
    <div>
      <h1>Hello {fullName}</h1>
       <input type="text" 
       placeholder="Enter your Name"
       defaultValue="" onChange={inputEvent}
       value = {name}  
       />
       <button onClick={onsumbit}>Submit now</button> 
    </div>
 </> 
  );




}

